import "./App.css";

// function App() {
//   return <div className="App"></div>;
// }

// export default App;

import React, { useState, useEffect } from "react";

const API_URL = "https://api.example.com/items";

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = async () => {
    try {
      // Choose either API endpoint or local JSON file based on a condition
      const apiUrl =
        process.env.REACT_APP_USE_API === "true"
          ? "https://api.example.com/items"
          : "/data/items.json"; // Assuming the JSON file is in the public folder

      const response = await fetch(apiUrl);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setItems(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  console.log(items);
  const handleRefresh = async () => {
    setLoading(true);
    setError(null);

    try {
      await fetchData();
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && (
        <div>
          <ul>
            {items.map((item) => (
              // <li key={item.id}>
              //   <h3>{item.name}</h3>
              //   <p>{item.description}</p>
              // </li>
              <li key={item.id}>
                <strong>{item.name}</strong>: {item.description}
              </li>
            ))}
          </ul>
          <button onClick={handleRefresh}>Refresh</button>
        </div>
      )}
    </div>
  );
};

export default ItemList;
