import "./App.css";

import React, { useState, useEffect } from "react";
import { Button } from "@mui/material";
// const API_URL = "https://api.example.com/items";

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  const fetchData = async () => {
    try {
      // Choose either API endpoint or local JSON file based on a condition
      const apiUrl =
        process.env.REACT_APP_USE_API === "true"
          ? "https://api.example.com/items"
          : "/data/items.json"; // Assuming the JSON file is in the public folder

      const response = await fetch(apiUrl);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setItems(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  console.log(items);

  const handleRefresh = async () => {
    setLoading(true);
    setError(null);

    try {
      await fetchData();
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = items.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="container">
      <h1 className="text-center mb-4">Item List</h1>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      {!loading && !error && (
        <div className="item-container">
          <ul>
            {currentItems.map((item) => (
              <li key={item.id}>
                <h3>{item.name}</h3>
                <p>{item.description}</p>
              </li>
            ))}
          </ul>

          <nav className="mt-3">
            <ul className="pagination justify-content-center">
              {Array.from({
                length: Math.ceil(items.length / itemsPerPage),
              }).map((_, index) => (
                <li
                  key={index}
                  className={`page-item ${
                    index + 1 === currentPage ? "active" : ""
                  }`}
                >
                  <button
                    onClick={() => paginate(index + 1)}
                    className="page-link"
                  >
                    {index + 1}
                  </button>
                </li>
              ))}
            </ul>
          </nav>

          <Button
            sx={{ alignItems: "center" }}
            variant="contained"
            color="success"
            onClick={handleRefresh}
          >
            Refresh
          </Button>
        </div>
      )}
    </div>
  );
};

export default ItemList;
